import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";

const ConfusionMatrix = ({ matrix }) => {
  const ref = useRef();

  useEffect(() => {
    if (!matrix) return;

    const svg = d3.select(ref.current);
    svg.selectAll("*").remove();

    const width = 300, height = 300;
    const cellSize = width / 2;

    const colors = d3.scaleSequential(d3.interpolatePiYG).domain([0, d3.max(matrix.flat())]);

    svg.attr("width", width).attr("height", height);

    svg.selectAll("rect")
      .data(matrix.flat())
      .enter()
      .append("rect")
      .attr("x", (d, i) => (i % 2) * cellSize)
      .attr("y", (d, i) => Math.floor(i / 2) * cellSize)
      .attr("width", cellSize - 2)
      .attr("height", cellSize - 2)
      .attr("fill", d => colors(d));

    svg.selectAll("text")
      .data(matrix.flat())
      .enter()
      .append("text")
      .attr("x", (d, i) => (i % 2) * cellSize + cellSize / 2)
      .attr("y", (d, i) => Math.floor(i / 2) * cellSize + cellSize / 2)
      .attr("text-anchor", "middle")
      .attr("alignment-baseline", "central")
      .attr("fill", "white")
      .text(d => d);
  }, [matrix]);

  return <svg ref={ref}></svg>;
};

const FeatureImportance = ({ features }) => {
  const ref = useRef();

  useEffect(() => {
    if (!features) return;

    const svg = d3.select(ref.current);
    svg.selectAll("*").remove();

    const width = 500, height = 300;
    const margin = { top: 20, right: 30, bottom: 50, left: 150 };

    const xScale = d3.scaleLinear()
      .domain([0, d3.max(features, d => d.importance)])
      .range([0, width - margin.left - margin.right]);

    const yScale = d3.scaleBand()
      .domain(features.map(d => d.name))
      .range([0, height - margin.top - margin.bottom])
      .padding(0.3);

    svg.attr("width", width).attr("height", height);

    const g = svg.append("g").attr("transform", `translate(${margin.left}, ${margin.top})`);

    g.selectAll("rect")
      .data(features)
      .enter()
      .append("rect")
      .attr("y", d => yScale(d.name))
      .attr("width", d => xScale(d.importance))
      .attr("height", yScale.bandwidth())
      .attr("fill", "#69b3a2");

    g.append("g").call(d3.axisLeft(yScale));
    g.append("g").attr("transform", `translate(0, ${height - margin.bottom})`).call(d3.axisBottom(xScale));
  }, [features]);

  return <svg ref={ref}></svg>;
};

const App = () => {
  const [report, setReport] = useState(null);
  const [matrix, setMatrix] = useState(null);
  const [features, setFeatures] = useState(null);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/test/ml")
      .then(res => res.json())
      .then(data => {
        setReport(data.report);
        setMatrix(data.conf_matrix);
        setFeatures(data.feature_importance);
      })
      .catch(err => console.error("Error fetching data:", err));
  }, []);

  return (
    <div>
      <h1>Confusion Matrix</h1>
      {matrix ? <ConfusionMatrix matrix={matrix} /> : <p>Loading...</p>}
      <h1>Feature Importance</h1>
      {features ? <FeatureImportance features={features} /> : <p>Loading...</p>}
    </div>
  );
};

export default App;
