import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [report, setReport] = useState(null);
  const [confMatrixUrl, setConfMatrixUrl] = useState("");
  const [featureImportanceUrl, setFeatureImportanceUrl] = useState("");

  useEffect(() => {
    fetch("http://127.0.0.1:5000/test/ml")
      .then(res => res.json())
      .then(data => {
        setReport(data.report);
        setConfMatrixUrl(data.conf_matrix_url);
        setFeatureImportanceUrl(data.feature_importance_url);
      })
      .catch(err => console.error("Error fetching data:", err));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Classification Report</h1>
        {report ? (
          <table border="1">
            <thead>
              <tr>
                <th>Metric</th>
                <th>Precision</th>
                <th>Recall</th>
                <th>F1-Score</th>
                <th>Support</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(report).map((label, index) => {
                if (typeof report[label] === "object") {
                  return (
                    <tr key={index}>
                      <td>{label}</td>
                      <td>{report[label].precision?.toFixed(2)}</td>
                      <td>{report[label].recall?.toFixed(2)}</td>
                      <td>{report[label]["f1-score"]?.toFixed(2)}</td>
                      <td>{report[label].support}</td>
                    </tr>
                  );
                }
                return null;
              })}
            </tbody>
          </table>
        ) : (
          <p>Loading...</p>
        )}

        <h2>Confusion Matrix</h2>
        {confMatrixUrl ? <img src={confMatrixUrl} alt="Confusion Matrix" width="400" /> : <p>Loading...</p>}

        <h2>Feature Importance</h2>
        {featureImportanceUrl ? <img src={featureImportanceUrl} alt="Feature Importance" width="400" /> : <p>Loading...</p>}
      </header>
    </div>
  );
}

export default App;
